CREATE TABLE `admins` (
  `aid` INT(5) NOT NULL AUTO_INCREMENT,
  `ausername` VARCHAR(10) NOT NULL,
  `apassword` VARCHAR(10) DEFAULT NULL,
  PRIMARY KEY (`aid`)
) ENGINE=INNODB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

