INSERT  INTO `message`(`mid`,`mname`,`mtele`,`mqq`,`mcontent`,`mtype`,`mdate`) VALUES (1,'李同学','18002134871','2987162401','听说科协下周要举行焊接比赛，很期待，祝你们活动顺利，赞！','个人留言','2015-03-03 00:00:00'),(2,'天津xx科协公司','18192318461','xx@sina.com','  我们公司现缺少...欲与贵社展开交流，同时提供单片机等比赛设备！','企业留言','2015-03-03 18:18:57');

